			<view @click="OnReturnenterpriseInfo">
				<image src="https://oss.dcqcjlb.com/51che_java_dev/20230328/file_1679972161006.png" style="width: 120rpx;height: 145rpx;">
				</image>
			</view>
			<view class="transition-line" style="margin-right: -20rpx;">
				<image src="@/pages/Settlein/static/image/guoduxian.png" style="width: 60rpx;height: 10rpx;"></image>
			</view>
			<view @click="OnReturnuploadAccountOpening">
				<image src="data:image/png;base64," style="width: 140rpx;height: 145rpx;">
				</image>
			</view>
			<view class="transition-line" style="margin-left: -19rpx;">
				<image src="@/pages/Settlein/static/image/guoduxian.png" style="width: 60rpx;height: 10rpx;"></image>
			</view>
			<view @click="OnPushagreement">
				<image src="https://oss.dcqcjlb.com/51che_java_dev/20230328/file_1679972117890.png" style="width: 120rpx;height: 145rpx;">
				</image>
			</view>
		</view>
		<view class="card-item">
			<view>
				企业入驻进度
			</view>
			<view style="color: #D91B1B;">
				<text v-if="type==1">企业账户认证中</text>
				<text v-else-if="type==2">待审核</text>
				<text v-else-if="type==3">入驻成功</text>
					<text v-else>驳回</text>
			</view>
		</view>
		<view v-if="type==1&&isLineLedger==1" class="card-item">
			<view>
				企业账户认证
			</view>
			<view @click="OnshowOntransAmt" style="color: #999999;display: flex;">
				<view>
					待打款金额确认
				</view>
				<view style="margin-top: 4rpx;">
					<u-icon size="14" name="arrow-right" :bold='true' color="#999999"></u-icon>
				</view>
			</view>
		</view>
		<view v-if="(type==2||type==3)&&isLineLedger==1" class="card-item">
			<view>
				企业账户认证
			</view>
			<view  style="color: #999999;display: flex;">
				<view>
					已确认
				</view>
				</view>
		</view>
		<view class="card-reject" v-if="type==4">
			驳回理由：<text style="color: #D91B1B;">{{auditReason}}</text>
		</view>
		<view  v-if="(type==1||type==2)&&isLineLedger==1" style="text-align: center;font-size:24rpx;margin-top: 40rpx;">
			如果您已确认打款金额但未收到短信，请<text style="color:#D91B1B ;" @click="resendMessage">《点击此处》</text>
		</view>
			<view style="position: fixed;bottom: 220rpx;">
		<view style="margin: 25rpx;font-size: 24rpx;">
			请从应用市场或复制链接到浏览器打开下载俱乐部商家APP及时查看入驻进度并体验相关功能
		</view>
		<view class="copy-btn" @click="Oncopy">
			复制链接
		</view>
		</view>
		<u-popup :safeAreaInsetBottom='false' :show="poptransAmt" @close="poptransAmt=false" mode="center">
			<view class="pop">
				<view class="pop-title">
					企业账户银联资金认证
				</view>
				<view class="pop-item">
					<view style="font-size: 24rpx;font-weight: bold;line-height: 48rpx;">
						确认金额：
					</view>
					<view>
						<u-input placeholderStyle="fontSize:24rpx" v-model="transAmt" placeholder="请输入收到的银联打款金额">
						</u-input>
					</view>
				</view>
				<view class="pop-btn">
					<text @click="OnamountVerification">提交</text>
				</view>
			</view>
			<u-toast ref="uToastpop"></u-toast>
			<u-toast ref="uToast"></u-toast>
		</u-popup>
		<u-toast ref="uToastpop"></u-toast>
		<u-toast ref="uToast"></u-toast>
		<view class="bottom-btn" v-if="type==4&&unionPayStatus==2">
			<text @click="Onagain">
				重新提交
			</text>
		</view>
				<u-modal confirmColor='red' :show="showmodal"  content="银联打款金额确认提交后，您的手机将收到视频认证短信，请您按照短信息提示点击相应链接地址完成银联签约认证。" @confirm='Ontruemodal'></u-modal>
	</view>
</template>

<script>
	export default {
		components: {

		},
		data() {
			return {
				showmodal:false,
				unionPayStatus:'',
				auditReason:'',
				transAmt: '',
				poptransAmt: false,
				type: 1 ,//1=待打款 2=待审核并已验证打款
				isLineLedger:null,
				downlist:{}
			}
		},

		onLoad(option) {
			this.type = option.type
		if(option.auditReason){
			this.auditReason=option.auditReason
		}
		if(option.unionPayStatus){
			this.unionPayStatus=option.unionPayStatus
		}
		if(option.isLineLedger){
			this.isLineLedger=option.isLineLedger
		}
		this.getdown()
		},
		onShow() {},
		methods: {
			getdown(){
				this.$newrequest.get("/coc-active/api/v1/merchantsettledin/appDownloadUrl").then(res=>{
					this.downlist=res.data
				})
			},
			Oncopy(){
			let _this=this
				let port = uni.getSystemInfoSync().platform
				if (port == 'ios') {
				uni.setClipboardData({
					data: _this.downlist.iosUrl,
					success: function() {
						uni.$u.toast('复制成功,请在浏览器打开')
					}
				});
				} else if (port == 'android') {
				uni.setClipboardData({
					data: _this.downlist.androidUrl,
					success: function() {
						uni.$u.toast('复制成功,请在浏览器打开')
					}
				});
				} else {
					console.log('运行在开发者工具上');
				}
			},
			Ontruemodal(){
				this.showmodal=false
			},
			OnGoHome(){
				uni.switchTab({
					url: '/pages/home/my'
				});
			},
			Onagain(){
				uni.navigateTo({
					url: '/pages/Settlein/uploadEnterprise?shopType=3&isshow=2'
				})
			},
			OnPushagreement(){
				uni.navigateTo({
					url: '/pages/Settlein/agreement?isLineLedger='+this.isLineLedger
				});
			},
			OnamountVerification() {
				let _this=this
				if (this.transAmt) {
					this.$request.post("/coc-active/api/v1/merchantsettledin/amountVerification", {
						transAmt: this.transAmt
					}).then(res => {
						if (res.code == 200) {
							this.type=2
								_this.poptransAmt = false
								_this.showmodal = true
							// this.$refs.uToastpop.show({
							// 	message: '银联打款金额确认提交后，您的手机将收到视频认证短信，请您按照短信息提示点击相应链接地址完成银联签约认证。',
							// });
						} else {
							this.$refs.uToastpop.show({
								message: res.message,
							});
						}
					})
				} else {
					this.$refs.uToast.show({
						message: '请填写银行打款金额',
					});
				}
			},
			OnshowOntransAmt() {
				this.poptransAmt = true
			},
			OnReturnuploadAccountOpening(){
				uni.navigateTo({
					url: '/pages/Settlein/uploadAccountOpening?isshow=1'
				});
			},
			OnReturnenterpriseInfo(){
				uni.navigateTo({
					url: '/pages/Settlein/enterpriseInfo?isshow=1'
				});
			},
			OnReturnimg() {
				uni.navigateTo({
					url: '/pages/Settlein/uploadEnterprise?isshow=1'
				});
			},
			resendMessage() {
				this.$request.get("/coc-active/api/v1/merchantsettledin/resendMessage").then(res => {
					if (res.code == 200) {
						this.$refs.uToast.show({
							message: '已重新发送银联签署短信，请确认查收',
							duration: '10000'
						});
					} else {
						this.$refs.uToast.show({
							message: res.message,
							duration: '10000'
						});
					}
				})
			},
		}
	}
</script>

